import React from 'react';
// import { Switch, Route } from 'react-router-dom';
// import Home from './Views/Home';
// import Browse from './Views/Browse';
import Recipe from './Views/Recipe';
import MockData from './tests/MockData';

function createIngredient(ingredient) {
  const { name, unit, quantity } = ingredient;
  const hasUnit = unit !== undefined && unit !== null;
  const unitConvertable = hasUnit && unit.scaler !== undefined && unit.scaler !== null;
  return {
    name, unit, quantity, hasUnit, unitConvertable,
  };
}

function getAvailableUnits() {
  const { availableUnits } = MockData;
  return availableUnits;
}

function geteRecipeData() {
  const { recipe } = MockData;
  const {
    id, title, instructions, availableUnits,
  } = recipe;

  const ingredients = recipe.ingredients.map(ingredient => createIngredient(ingredient));
  recipe.ingredients = ingredients;
  return {
    id, title, instructions, availableUnits, ingredients, ...recipe,
  };
}

export default function App() {
  const recipe = geteRecipeData();
  const availableUnits = getAvailableUnits();
  recipe.availableUnits = availableUnits;
  const context = recipe;
  return (
    <Recipe context={context} />
  );
}
