import React from 'react';
import styled from 'styled-components';
import { Tab } from 'semantic-ui-react';
import { decimalToFraction } from '../utils';
import MockData from '../tests/MockData';
// import '../Css/Ingredient.css';

const IngredientTool = styled.div`
    display: flex;
    height: fit-content;
    border-top: 1px solid #3c567b;
`;

const Quantity = styled.span`
    display: flex;
    align-items: center;
    justify-content: center;
    padding: 0.5rem;
    font-size: 1rem;
`;

const IngredientDisplay = styled.div`
    flex-grow: 1;
    display: flex;
    align-items: center;
    justify-content: center;
    padding: 0.5rem;
    font-size: 1rem;
`;

const Unit = styled(Tab)`
    ${props => (props.hasUnit ? '' : 'display: none')};
    .menu {
        max-height: ${props => (props.open ? '5rem' : '0')};
        transition: max-height 0.2s ease-out;
        padding: 0;
    }

    .ui.attached.menu {
        min-height: 0;
    }
    .ui.tab.active, .ui.tab.open {
        border-radius: 0;
        border-top: none;
        border-left: 1px solid #3c567b;
        border-bottom: none;
        border-right: 1px solid #3c567b;
        padding: 0.75rem;
        font-size: 1rem;
        margin: 0;
        margin-left: -1px;
    }

    .ui.menu a.item {
        ${props => (props.open ? 'display: flex' : 'display: none')};
        border-right: 1px solid #3c567b;
    }

    .ui.attached+.ui.attached.menu:not(.top) {
        border-left: 1px solid #3c567b;
        border-bottom: none;
        border-right: none;
        border-radius: 0;
        ${props => (props.open ? 'border-top: 1px solid #3c567b;' : '')};
    }
`;

// const IngredientDisplay = styled(Tab)`
//     flex-direction: column;
//     height: 100%;
//     border-right: 1px solid #2E3E55;
//     display: flex;
//     align-items: center;
//     justify-content: center;

//     .menu {
//         max-height: ${props => (props.open? '5rem' : '0')};
//         transition: max-height 0.2s ease-out;
//         padding: 0;
//     }
//     .ui.tab.active, .ui.tab.open {
//         border-radius: 0;
//         border: 1px solid #3c567b;
//     }

//     .ui.menu a.item {
//         ${props => (props.open? 'display: flex' : 'display: none')};
//     }
// `;

// const RecipeIngredient = styled.div`
//     display: flex;
//     align-items: center;
//     border-top: 1px solid #2E3E55;
//     height: 3rem;
//     width: fit-content;
//     font-size: 1.5rem;
// `;

// const Quantity = styled.span`
//     border-right: 1px solid #2E3E55;
//     height: 100%;
//     display: flex;
//     align-items: center;
//     justify-content: center;
//     padding: 0.5rem;
// `;

// ui bottom attached segment active tab

function createIngredientDisplay(ingredient) {
  let unitDisplay;
  let ingredientDisplay = ingredient.name;
  const { unit } = ingredient;
  const noUnit = unit === null;

  if (noUnit) {
    unitDisplay = '';
    ingredientDisplay = ingredient.plural === undefined ? ingredient.name : ingredient.plural;
  } else if (ingredient.quantity <= 1) {
    unitDisplay = unit.name;
  } else {
    unitDisplay = unit.plural === undefined ? unit.plural : unit.name;
  }

  return { unitDisplay, ingredientDisplay };
}

export default function Ingredient(props) {
  const { ingredient } = props;
  const { availableUnits } = MockData.recipe;
  const quantity = decimalToFraction(ingredient.quantity, 8);
  const { ingredientDisplay } = createIngredientDisplay(ingredient);

  const [openTabs, setOpenTabs] = React.useState(false);
  const [selectedUnit, setSelectedUnit] = React.useState(ingredient.unit !== null ? ingredient.unit.id : 0);

  const panes = availableUnits.map(u => (
    {
      menuItem: u.name,
      render: () => <Tab.Pane key={u.id} onClick={() => { setOpenTabs(!openTabs); setSelectedUnit(u.id); }} open={openTabs} attached="top">{u.name}</Tab.Pane>,
    }
  ));

  return (
    <IngredientTool>
      <Quantity>{quantity}</Quantity>
      <Unit activeIndex={selectedUnit} hasUnit={ingredient.unit !== null} menu={{ attached: 'bottom' }} open={openTabs} panes={panes} />
      <IngredientDisplay>{ingredientDisplay}</IngredientDisplay>
    </IngredientTool>
  );
}
