import React from 'react';
import MockData from '../tests/MockData';

function createIngredient(ingredient) {
  const { name, unit, quantity } = ingredient;
  const hasUnit = unit !== undefined && unit !== null;
  const unitConvertable = hasUnit && unit.scaler !== undefined && unit.scaler !== null;
  return {
    name, unit, quantity, hasUnit, unitConvertable,
  };
}

function getAvailableUnits() {
  const { availableUnits } = MockData;
  return availableUnits;
}

function formatRecipeData(recipe) {
  const formattedRecipe = recipe;

  const ingredients = recipe.ingredients.map(ingredient => createIngredient(ingredient));
  formattedRecipe.ingredients = ingredients;
  return formattedRecipe;
}


export default function useRecipeData() {
  const { recipe } = MockData;
  const {
    id,
    title,
    subTitle,
    instructions,
    ingredients,
    ovenHeat,
    servings,
    appliances,
    image,
  } = formatRecipeData(recipe);
  const [scaler, setScaler] = React.useState(recipe.scaler);
  const [isCelsius, setIsCelsius] = React.useState(recipe.isCelsius);
  const availableUnits = getAvailableUnits();
  //   React.useEffect(() => {
  //     (async () => {
  //     });
  //   });

  return {
    scaler,
    setScaler,
    isCelsius,
    setIsCelsius,
    id,
    title,
    subTitle,
    instructions,
    availableUnits,
    ingredients,
    ovenHeat,
    servings,
    appliances,
    image,
  };
}
