import React, { useContext } from 'react';
import styled from 'styled-components';
import { Tab } from 'semantic-ui-react';
import { decimalToFraction, convertUnit } from '../utils';
import { mediumBlue, lightBlue } from '../Styles/constants';
import RecipeContext from '../Context/RecipeContext';

const IngredientTool = styled.div`
    display: flex;
    height: fit-content;
    border-top: 1px solid ${mediumBlue};
`;

const Quantity = styled.span`
    display: flex;
    align-items: center;
    justify-content: center;
    padding: 0.5rem;
    font-size: 1rem;
`;

const IngredientDisplay = styled.div`
    flex-grow: 1;
    display: flex;
    align-items: center;
    justify-content: center;
    padding: 0.5rem;
    font-size: 1rem;
`;

const Unit = styled(Tab)`
    ${props => (props.hasUnit ? '' : 'display: none')};

    .menu {
        ${props => (props.unitConvertable ? '' : 'display: none')};
        max-height: ${props => (props.open ? '5rem' : '0')};
        transition: max-height 0.2s ease-out;
        padding: 0;
    }

    .ui.attached.menu {
        min-height: 0;
    }
    .ui.tab.active, .ui.tab.open {
        border-radius: 0;
        border-top: none;
        border-left: 1px solid ${mediumBlue};
        border-bottom: none;
        border-right: 1px solid ${mediumBlue};
        padding: 0.75rem;
        font-size: 1rem;
        margin: 0;
        margin-left: -1px;
    }

    .ui.menu {
      a.active.item {
        background-color: ${lightBlue};

        :hover {
          background-color: ${lightBlue};
        }
      }

      a.item {
        ${props => (props.open ? 'display: flex' : 'display: none')};
        border-right: 1px solid ${mediumBlue};

        :hover {
          background-color: rgba(0, 0, 0, 0.09);
        }
      }
    }

    .ui.attached+.ui.attached.menu:not(.top) {
        border-left: 1px solid ${mediumBlue};
        border-bottom: none;
        border-right: none;
        border-radius: 0;
        ${props => (props.open ? `border-top: 1px solid ${mediumBlue}` : '')};
    }

    .ui.bottom.attached.menu>.item:first-child {
      border-radius: 0;
  }
`;

function createIngredientDisplay(ingredient) {
  let unitDisplay;
  let ingredientDisplay = ingredient.name;
  const { unit } = ingredient;
  const noUnit = unit === null;

  if (noUnit) {
    unitDisplay = '';
    ingredientDisplay = ingredient.plural === undefined ? ingredient.name : ingredient.plural;
  } else if (ingredient.quantity <= 1) {
    unitDisplay = unit.name;
  } else {
    unitDisplay = unit.plural === undefined ? unit.plural : unit.name;
  }

  return { unitDisplay, ingredientDisplay };
}

function Ingredient(props) {
  const { ingredient } = props;
  const { availableUnits, scaler } = useContext(RecipeContext);
  const [selectedUnit, setSelectedUnit] = React.useState(
    ingredient.hasUnit ? ingredient.unit.id : 0,
  );
  const [openTabs, setOpenTabs] = React.useState(false);
  const [quantity, setQuantity] = React.useState(ingredient.quantity * 1);

  // const quantity = convertUnit(ingredient.quantity,
  // availableUnits[selectedUnit].scaler, ingredient.unit.scaler);
  const quantityDisplay = decimalToFraction(quantity, 1000);
  const { ingredientDisplay } = createIngredientDisplay(ingredient);

  const toggleTab = () => setOpenTabs(!openTabs);

  const handleTabChange = (e, { activeIndex }) => {
    const newUnit = availableUnits[activeIndex];
    const oldUnit = availableUnits[selectedUnit];
    setQuantity(convertUnit(quantity, newUnit.scaler, oldUnit.scaler));
    setSelectedUnit(newUnit.id);
  };

  const panes = availableUnits.map(u => (
    {
      menuItem: u.name,
      render: () => (
        <Tab.Pane
          key={u.id}
          onClick={toggleTab}
          open={openTabs}
          attached="top"
        >
          {u.name}
        </Tab.Pane>
      ),
    }
  ));

  if (ingredient.hasUnit && !ingredient.unitConvertable) {
    panes[ingredient.unit.id] = {
      menuItem: ingredient.unit.name,
      render: () => (
        <Tab.Pane
          key={ingredient.unit.id}
          onClick={toggleTab}
          open={openTabs}
          attached="top"
        >
          {ingredient.unit.name}
        </Tab.Pane>
      ),
    };
  }

  return (
    <IngredientTool>
      <Quantity>{quantityDisplay}</Quantity>
      <Unit
        defaultActiveIndex={selectedUnit}
        onTabChange={handleTabChange}
        hasUnit={ingredient.hasUnit}
        unitConvertable={ingredient.unitConvertable}
        menu={{ attached: 'bottom' }}
        open={openTabs}
        panes={panes}
      />
      <IngredientDisplay>{ingredientDisplay}</IngredientDisplay>
    </IngredientTool>
  );
}

export default Ingredient;
